  controller: 'userAddServiceCtrl',
  onEnter: showAddServiceModal
})
//onEnter中写入需要弹出的弹出框地址
function showAddServiceModal($modal, $previousState) {
  $modal.open({
    templateUrl: 'views/user/features/service/addService.html',
    backdrop: 'static',
    controller: ''
  })
}
